create table clientes (id bigint not null auto_increment, cpf varchar(20), email varchar(100), id_cli_base bigint, nome varchar(150), publicidade_email bit, publicidade_sms bit, telefone_celular varchar(50), tipo varchar(50), primary key (id)) engine=InnoDB;
create table config_app (id bigint not null auto_increment, client_id_ifood_mercado varchar(255), client_secret_ifood_mercado varchar(255), expire_in datetime, token TEXT, primary key (id)) engine=InnoDB;
create table enderecos_cliente (id bigint not null auto_increment, numero_endereco_cliente varchar(10), cliente_id bigint, endereco_id bigint, primary key (id)) engine=InnoDB;
create table eventos_pedidos_loja (id bigint not null, codigo_pedido varchar(50), id_loja integer, status varchar(10), primary key (id)) engine=InnoDB;
create table itens_pedido (id bigint not null, codigo varchar(50), codigo_barra varchar(20), codigo_loja varchar(50), desistencia bit, id_item_pedido bigint, index varchar(255), indisponivel bit, observacao varchar(255), peso_variavel bit, plu varchar(50), produto varchar(255), quantidade integer, quantidade3 integer, valor decimal(19,2), valor_total decimal(19,2), pedido_id bigint, primary key (id)) engine=InnoDB;
create table logradouros (id bigint not null auto_increment, bairro varchar(100) not null, cep varchar(10) not null, cidade varchar(100) not null, ibge varchar(10) not null, latitude decimal(19,2), logradouro varchar(255) not null, longitude decimal(19,2), uf varchar(50) not null, uf_sigla varchar(2) not null, pais_id integer not null, primary key (id)) engine=InnoDB;
create table loja (id bigint not null, cnpj varchar(20), nome varchar(100), numero_enredeco_loja varchar(10), rede_id bigint, rede_nome varchar(150), status varchar(20), endereco_id bigint, primary key (id)) engine=InnoDB;
create table pais (id integer not null auto_increment, nome varchar(50), primary key (id)) engine=InnoDB;
create table pedidos_integracao (id bigint not null auto_increment, agendamento_data_fim datetime, agendamento_data_inicio datetime, agendamento_hora_fim time, agendamento_hora_inicio time, codigo varchar(50), codigo_loja varchar(50), cpf_na_nota bit, data datetime, data_hora datetime, entrega bit, hora time, id_cliente bigint, id_loja bigint, numero_endereco_entrega varchar(10), parceiro_codigo_entrega varchar(50), parceiro_codigo_pedido varchar(50), pessoa_autorizada_recebimento varchar(100), plataforma varchar(100), quantidade_item_unico integer, status varchar(10), status_descricao varchar(30), valor_conveniencia decimal(19,2), valor_corrigido decimal(19,2), valor_desconto decimal(19,2), valor_entrega decimal(19,2), valor_mercado decimal(19,2), valor_retirada decimal(19,2), valor_total decimal(19,2), valor_troco decimal(19,2), cliente_id bigint, endereco_id bigint, loja_id bigint, primary key (id)) engine=InnoDB;
create table produtos_integracao (id bigint not null auto_increment, ativo bit, categoria varchar(100), codigo_barra integer, data_ultima_itegracao datetime, departamento varchar(100), descricao TEXT, id_loja bigint, imageurl varchar(150), integrar bit, marca varchar(100), nome varchar(150), plu integer, quantidade_atacado decimal(19,2), quantidade_estoque_atual decimal(19,2), quantidade_estoque_minimo decimal(19,2), sub_categoria varchar(100), unidade varchar(100), validade_proxima bit, valor decimal(19,2), valor_atacado decimal(19,2), valor_compra decimal(19,2), valor_promocao decimal(19,2), volume varchar(100), primary key (id)) engine=InnoDB;
